sys_script_client.script:
var targetText = "";
/*
*
*    Verifier la reference
*
*/

//get the first line of the mail to get the body
var reference = targetText.split("\n")[0].trim(); 
//check if the first line has the RITM sting and 7 other numbers
var re = /^RITM\d{7}/;
var isReference = re.test(reference);
console.log("is ref valid = "+ re.test(isReference));



/*
*
*    Parser la liste
*
*/
// seprarer les ligne par les bulletes
var reg = new RegExp(/● ([^●]*)/g);
//separer le nom de la variable par les ( )
var reg2 = new RegExp(/\((.*?)\)/);
//separer le value par les [ ]
var reg3 = new RegExp(/\[(.*?)\]/);

// Array de tous les les lignes
var result = targetText.match(reg);

// Boucler par tous les lignes et pour trouver tous les pair cle valeur
result.forEach(function(element) {
  var key; 
  var value;
  // Verifier si les valuers sont pas NULL our ne pas bloquer le script; et utiliser la regEx pour trouver les bons valeurs
  if(reg2.exec(element) !== null) key = reg2.exec(element)[1];
  if(reg3.exec(element) !== null) value =  reg3.exec(element)[1];
	console.log(key,value);
});
