sys_script_client.script:
var targetText = "";


function findReference(){
	var result;
  //regEx to find the reference
  regReference = new RegExp(/\bRITM\d{7}\b/);
	//separate every line of the mail
	targetText.split("\n").forEach(function(element, index){
  	//if he finds a refence he returns it; it returns ONLY the first valid reference
    if(regReference.test(element)){

    	if(result == undefined)
      result = regReference.exec(element)[0];

    }
  });
  return result;
}

var reference = findReference();
console.log("reference: "+reference);


/*
*
*    Parser la liste
*
*/
// seprarer les ligne par les bulletes
var reg = new RegExp(/● ([^●]*)/g);
//separer le nom de la variable par les ( )
var reg2 = new RegExp(/\((.*?)\)/);
//separer le value par les [ ]
var reg3 = new RegExp(/\[(.*?)\]/);

// Array de tous les les lignes
var result = targetText.match(reg);
var listKeyValue= [];
//if the mail has 1+ list items we will parce them
if(result.length > 1){

    // Boucler par tous les lignes et pour trouver tous les pair cle valeur
  result.forEach(function(element) {
    var key; 
    var value;
    // Verifier si les valuers sont pas NULL our ne pas bloquer le script; et utiliser la regEx pour trouver les bons valeurs
    if(reg2.exec(element) !== null) key = reg2.exec(element)[1];
    if(reg3.exec(element) !== null) value =  reg3.exec(element)[1];
    listKeyValue[key] = value;
  });

}
console.table(listKeyValue);
